function assignFilters() {
    $("$input[type=checkbox]").change(filter(this));
}
function filter(radio) {
    var r = radio.value;
	console.log(r.value);
    if (r == "all" && r.checked == "checked") {
        $("tr.dataRow").css("visibility", "visible");
    }
    else if (r == "all" && r.checked == "checked") {
		if (r.checked == "checked") {
        	$("tr.dataRow").css("visibility", "visible");
		}
		else {
			$("tr.dataRow").css("visibility", "hidden");
		}
    }
    else {

    }
}

