function setDataToTable(jsonData){
	$('#cards').DataTable( {
        pagination: "bootstrap",
            filter: true,
            data: jsonData.data,
            destroy: true,
            lengthMenu: [10,25,50,75,100],
            pageLength: 50,
            "columns":
            [  
                {"data": "name"},
                {"data": "class"},
                {"data": "type"},
                {"data": "rarity"},
                {"data": "cost"},
                {"data": "desc"},
                {"data": "udesc"}
            ]
        } 
    );
}